const http = require('http');

const server = http.createServer((req, res) => {
  if (req.url === '/') {
    // Página principal con una imagen
    res.statusCode = 200;
    res.setHeader('Content-Type', 'text/html');
    res.end(`
      <!DOCTYPE html>
      <html lang="es">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Página Principal</title>
      </head>
      <body>
        <h1>Bienvenido a la Página Principal</h1>
        <p>Esta es la página principal del sitio.</p>
        <img src="data:image/png;base64,"
        alt="Imagen de ejemplo">
      </body>
      </html>
    `);
  }else if (req.url === '/new-page') {
    // Nueva página con una imagen
    res.statusCode = 200;
    res.setHeader('Content-Type', 'text/html');
    res.end(`
      <!DOCTYPE html>
      <html lang="es">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Nueva Página</title>
      </head>
      <body>
        <h1>Esta es la nueva página</h1>
        <p>Contenido de la nueva página.</p>
        <img src="data:image/jpeg;base64," 
        alt="Imagen de ejemplo en la nueva página">
      </body>
      </html>
    `);
  } else {
    // Página no encontrada
    res.statusCode = 404;
    res.setHeader('Content-Type', 'text/plain');
    res.end('Página no encontrada');
  }
});

const port = 3002;
server.listen(port, () => {
  console.log(`El servidor está escuchando en http://localhost:${port}`);
});
