const http = require('http');

const server = http.createServer((req, res) => {
  if (req.url === '/') {
    // P치gina principal con una imagen
    res.statusCode = 200;
    res.setHeader('Content-Type', 'text/html');
    res.end(`
      <!DOCTYPE html>
      <html lang="es">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>P치gina Principal</title>
      </head>
      <body>
        <h1>Bienvenido a la P치gina Principal</h1>
        <p>Esta es la p치gina principal del sitio.</p>
        <img src="data:image/png;base64,"
        alt="Imagen de ejemplo">
      </body>
      </html>
    `);
  }else if (req.url === '/new-page') {
    // Nueva p치gina con una imagen
    res.statusCode = 200;
    res.setHeader('Content-Type', 'text/html');
    res.end(`
      <!DOCTYPE html>
      <html lang="es">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Nueva P치gina</title>
      </head>
      <body>
        <h1>Esta es la nueva p치gina</h1>
        <p>Contenido de la nueva p치gina.</p>
        <img src="data:image/jpeg;base64," 
        alt="Imagen de ejemplo en la nueva p치gina">
      </body>
      </html>
    `);
  } else {
    // P치gina no encontrada
    res.statusCode = 404;
    res.setHeader('Content-Type', 'text/plain');
    res.end('P치gina no encontrada');
  }
});

const port = 3002;
server.listen(port, () => {
  console.log(`El servidor est치 escuchando en http://localhost:${port}`);
});
